ListingCategoriesPage:
fragment ListingCategoryRow on ListingCategory {
  id
  name
  path
}

fragment Paging on Paging {
  page
  pageSize
  totalEntities
  totalPages
}


query ListingCategoriesPage {
  listingCategories {
    entities {
      ...ListingCategoryRow
    }
    paging {
      ...Paging
    }
  }
}
